// @ts-check
const jikan_api_airing = "https://api.jikan.moe/v4/top/anime?filter=airing&type=tv&page=1&limit=7";

async function getAiringAnime() {
	const response = await fetch(jikan_api_airing);
    const data = await response.json();
    data.forEach((/** @type {{ images: { webp: { image_url: any; }; }; title: any; url: any; }} */ anime) => {
      const anime_image = anime.images.webp.image_url;
      const anime_title = anime.title;
      const anime_url = anime.url;
      const card = document.createElement("div");
      card.classList.add("anime-card");
      card.innerHTML = `
      <div class="banner"><img src="${anime_image}" text="${anime_title}"></img></div>
      <div class="title"><a href="${anime_url}" target="_blank">${anime_title}</a></div>
      `;
      document.querySelector(".container-airing-cards")?.appendChild(card);
    });
}
setTimeout(() => { getAiringAnime(); }, 2000);

(function fetchAPI(api) {
    fetch(api).then(response => response.json()).then(data => {
        data.data.forEach((/** @type {{ images: { webp: { image_url: any; }; }; title: String; url: any; }} */ anime) => {
            const image = anime.images.webp.image_url;
            const title = anime.title;
            const url = anime.url;
            const card = document.createElement("div");
            card.classList.add("anime-card");
            card.innerHTML = `
            <div class="banner"><img src="${image}" text="${title}"></img></div>
            <div class="title"><a href="${url}" target="_blank">${title}</a></div>
            `;
            document.querySelector(".container-airing-cards")?.appendChild(card);
        });
    }).catch(error => console.error(error));
})(api_airing);
